#!/usr/bin/env bash
PATH=/bin:/sbin:/usr/bin:/usr/sbin:/usr/local/bin:/usr/local/sbin:~/bin && export PATH
## 小米运动app步数不会更新,仅更新关联应用:我的->第三方接入->绑定支付宝,微信
### 微信排行榜开关:微信-设置-通用-辅助功能-微信运动-隐私及提醒设置
#### 支付宝排行榜开关:支付宝运动-...-设置  

# tempfile & rm it when exit
trap 'rm -f "$TMPFILE"' EXIT; TMPFILE=$(mktemp) || exit 1

# 获取mimotion修改步数所需参数,账号@密码@随机步数起止点,可多次传入支持多账号:18812345678@password@10000-20000 19912345678@password@20000-25000
all_parameter=($(echo $@)) && [[ ${all_parameter[*]} == "" ]] && echo 获取传入参数失败 && exit 1
all_username_password=($(echo ${all_parameter[*]} | grep -oE "1[0-9]{10}@[a-zA-Z0-9]{8,}@[0-9]+-[0-9]+" | sort -u | tr "\n" " "))
[[ $all_username_password == "" ]] && echo "mimotion参数错误" && exit 1

# deviceId: 随机设备ID,也可使用传入参数 deviceId@*************
deviceId=$(tr -dc '0-9' </dev/urandom | head -c 999999 | grep -oE "[1-9][0-9]+" | head -c 15)
echo ${all_parameter[*]} | grep -qE "deviceId@[0-9]+" && deviceId=$(echo ${all_parameter[*]} | grep -oE "deviceId@[0-9]+" | cut -f2 -d@)

# app版本和UA
app_version="5.0.1" && UA="MiFit/${app_version} (iPhone; iOS 14.4.1; Scale/3.00)"

function urlencode() {
    local length="${#1}"
    for (( i = 0; i < length; i++ )); do
        local c="${1:i:1}"
        case $c in
            [a-zA-Z0-9.~_-]) printf "$c" ;;
            *) printf "$c" | xxd -p -c1 | while read x;do printf "%%%s" "$x";done
        esac
    done
}

function mimotion() {
    # login
    data="phone_number=%2B86${username}&password=${password}&state=REDIRECTION&client_id=HuaMi&country_code=CN&token=access&token=refresh&redirect_uri=https%3A%2F%2Fs3-us-west-2.amazonaws.com%2Fhm-registration%2Fsuccesssignin.html"
    access=$(curl -isA "$UA" --data "$data" "https://api-user.huami.com/registrations/%2B86${username}/tokens" | grep -oE "^location:.*" | grep -oE "access=[^&]{10,}" | cut -d= -f2)
    data="dn=account.huami.com%2Capi-user.huami.com%2Cauth.huami.com%2Capi-analytics.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com%2Capi-watch.huami.com&app_version=${app_version}&source=com.xiaomi.hm.health%3A${app_version}&country_code=CN&device_id=${deviceId}&third_name=huami_phone&lang=zh&device_model=android_phone&allow_registration=false&app_name=com.xiaomi.hm.health&code=${access}&grant_type=access_token&"
    curl -X POST -sA "$UA" --data "$data" https://account.huami.com/v2/client/login >$TMPFILE
    # modify
    user_id=$(cat $TMPFILE | grep -oE "user_id\":\"[^\"]+" | cut -d\" -f3)
    app_token=$(cat $TMPFILE | grep -oE "app_token\":\"[^\"]+" | cut -d\" -f3)
    data="userid=${user_id}&last_sync_data_time=$(date +%s -d "-$(shuf -i 1-12 -n1)hour")&device_type=0&last_deviceid=DA932FFFFE8816E7&data_json=%5B%7B%22data_hr%22%3A%22%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F9L%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2FVv%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F0v%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F9e%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F0n%5C%2Fa%5C%2F%5C%2F%5C%2FS%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F0b%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F1FK%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2FR%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F9PTFFpaf9L%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2FR%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F0j%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F9K%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2FOv%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2Fzf%5C%2F%5C%2F%5C%2F86%5C%2Fzr%5C%2FOv88%5C%2Fzf%5C%2FPf%5C%2F%5C%2F%5C%2F0v%5C%2FS%5C%2F8%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2FSf%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2Fz3%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F0r%5C%2FOv%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2FS%5C%2F9L%5C%2Fzb%5C%2FSf9K%5C%2F0v%5C%2FRf9H%5C%2Fzj%5C%2FSf9K%5C%2F0%5C%2F%5C%2FN%5C%2F%5C%2F%5C%2F%5C%2F0D%5C%2FSf83%5C%2Fzr%5C%2FPf9M%5C%2F0v%5C%2FOv9e%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2FS%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2F%5C%2Fzv%5C%2F%5C%2Fz7%5C%2FO%5C%2F83%5C%2Fzv%5C%2FN%5C%2F83%5C%2Fzr%5C%2FN%5C%2F86%5C%2Fz%5C%2F%5C%2FNv83%5C%2Fzn%5C%2FXv84%5C%2Fzr%5C%2FPP84%5C%2Fzj%5C%2FN%5C%2F9e%5C%2Fzr%5C%2FN%5C%2F89%5C%2F03%5C%2FP%5C%2F89%5C%2Fz3%5C%2FQ%5C%2F9